import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [summary, setSummary] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setSummary('');
    setError('');
  };

  const handleUpload = async () => {
    if (!file) {
      setError('Please select a PDF file to upload.');
      return;
    }

    setLoading(true);
    setSummary('');
    setError('');

    const formData = new FormData();
    formData.append('resume', file);

    try {
      const response = await axios.post('http://localhost:5000/api/upload', formData);
      setSummary(response.data.summary);
    } catch (err) {
      setError('Failed to summarize resume. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center px-4">
      <div className="bg-white p-8 rounded-2xl shadow-xl w-full max-w-xl">
        <h1 className="text-2xl font-bold mb-4 text-center">Resume Summarizer</h1>

        <input
          type="file"
          accept=".pdf"
          onChange={handleFileChange}
          className="block w-full mb-4"
        />

        <button
          onClick={handleUpload}
          className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 transition"
        >
          {loading ? 'Summarizing...' : 'Upload and Summarize'}
        </button>

        {error && <p className="text-red-500 mt-4">{error}</p>}

        {summary && (
          <div className="mt-6 p-4 bg-gray-50 border rounded max-h-80 overflow-y-auto">
            <h2 className="font-semibold mb-2">Summary:</h2>
            <p className="text-gray-800 whitespace-pre-line">{summary}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
